from __future__ import absolute_import, division, unicode_literals
from future.builtins import str
from future.backports import urllib
from future.backports.urllib import parse as _parse, request as _request
urllib.parse = _parse
urllib.request = _request
__all__ = [
 b'RobotFileParser']

class RobotFileParser(object):

    def __init__(self, url=b''):
        self.entries = []
        self.default_entry = None
        self.disallow_all = False
        self.allow_all = False
        self.set_url(url)
        self.last_checked = 0
        return

    def mtime(self):
        return self.last_checked

    def modified(self):
        import time
        self.last_checked = time.time()

    def set_url(self, url):
        self.url = url
        self.host, self.path = urllib.parse.urlparse(url)[1:3]

    def read(self):
        try:
            f = urllib.request.urlopen(self.url)
        except urllib.error.HTTPError as err:
            if err.code in (401, 403):
                self.disallow_all = True
            elif err.code >= 400:
                self.allow_all = True
        else:
            raw = f.read()
            self.parse(raw.decode(b'utf-8').splitlines())

    def _add_entry(self, entry):
        if b'*' in entry.useragents:
            if self.default_entry is None:
                self.default_entry = entry
        else:
            self.entries.append(entry)
        return

    def parse(self, lines):
        state = 0
        entry = Entry()
        for line in lines:
            if not line:
                if state == 1:
                    entry = Entry()
                    state = 0
                elif state == 2:
                    self._add_entry(entry)
                    entry = Entry()
                    state = 0
            i = line.find(b'#')
            if i >= 0:
                line = line[:i]
            line = line.strip()
            if not line:
                continue
            line = line.split(b':', 1)
            if len(line) == 2:
                line[0] = line[0].strip().lower()
                line[1] = urllib.parse.unquote(line[1].strip())
                if line[0] == b'user-agent':
                    if state == 2:
                        self._add_entry(entry)
                        entry = Entry()
                    entry.useragents.append(line[1])
                    state = 1
                elif line[0] == b'disallow':
                    if state != 0:
                        entry.rulelines.append(RuleLine(line[1], False))
                        state = 2
                elif line[0] == b'allow':
                    if state != 0:
                        entry.rulelines.append(RuleLine(line[1], True))
                        state = 2

        if state == 2:
            self._add_entry(entry)

    def can_fetch(self, useragent, url):
        if self.disallow_all:
            return False
        if self.allow_all:
            return True
        parsed_url = urllib.parse.urlparse(urllib.parse.unquote(url))
        url = urllib.parse.urlunparse((b'', b'', parsed_url.path,
         parsed_url.params, parsed_url.query, parsed_url.fragment))
        url = urllib.parse.quote(url)
        if not url:
            url = b'/'
        for entry in self.entries:
            if entry.applies_to(useragent):
                return entry.allowance(url)

        if self.default_entry:
            return self.default_entry.allowance(url)
        return True

    def __str__(self):
        return (b'').join([ str(entry) + b'\n' for entry in self.entries ])


class RuleLine(object):

    def __init__(self, path, allowance):
        if path == b'' and not allowance:
            allowance = True
        self.path = urllib.parse.quote(path)
        self.allowance = allowance

    def applies_to(self, filename):
        return self.path == b'*' or filename.startswith(self.path)

    def __str__(self):
        return (self.allowance and b'Allow' or b'Disallow') + b': ' + self.path


class Entry(object):

    def __init__(self):
        self.useragents = []
        self.rulelines = []

    def __str__(self):
        ret = []
        for agent in self.useragents:
            ret.extend([b'User-agent: ', agent, b'\n'])

        for line in self.rulelines:
            ret.extend([str(line), b'\n'])

        return (b'').join(ret)

    def applies_to(self, useragent):
        useragent = useragent.split(b'/')[0].lower()
        for agent in self.useragents:
            if agent == b'*':
                return True
            agent = agent.lower()
            if agent in useragent:
                return True

        return False

    def allowance(self, filename):
        for line in self.rulelines:
            if line.applies_to(filename):
                return line.allowance

        return True