from __future__ import unicode_literals
from lib2to3 import fixer_base
from libfuturize.fixer_util import touch_import_top
expression = b"name='execfile'"

class FixExecfile(fixer_base.BaseFix):
    BM_compatible = True
    run_order = 9
    PATTERN = (b"\n              power<\n                 ({0}) trailer< '(' args=[any] ')' >\n              rest=any* >\n              ").format(expression)

    def transform(self, node, results):
        name = results[b'name']
        touch_import_top(b'past.builtins', name.value, node)